mapper.select:
SELECT EXISTS(
    SELECT NICKNAME
    FROM USER_INFO
    WHERE NICKNAME = #{nickname}
)

SELECT UID, NICKNAME, INTRODUCE
FROM USER_INFO
WHERE UID = #{uid}

SELECT A.UID FROM USER_INFO A WHERE NOT EXISTS ( SELECT B.UID FROM (
SELECT #{item} AS UID
) B WHERE A.UID = B.UID ) ORDER BY ( SELECT COUNT(C.UID) FROM FEED C WHERE A.UID = C.UID ) DESC LIMIT 30
